    const addCourse = async () => {
        const data = await fetch(api_base + "/course/new/" + newCourse._id, { method: "POST" })
			.then(res => res.json());

        insertCourse([...selectedCourses, data]);

        setPopupActive(false);
        setNewCourse({});
    }

	const deleteCourse = async id => {
		const data = await fetch(api_base + '/course/delete/' + id, { method: "DELETE" })
			.then(res => res.json());

		insertCourse(selectedCourses => selectedCourses.filter(course => course._id !== data.result._id));
	}

