import { useEffect, useState } from 'react';
const api_base = 'http://localhost:3001';

function App() {
	const [popupActive, setPopupActive] = useState(false);
	const [sempopupActive, setSemPopupActive] = useState(false);
	const [newCourse, setNewCourse] = useState("");
	const [selectedCourses, insertCourse] = useState([]);
	// eslint-disable-next-line  no-unused-vars
	const [courses, setCourses] = useState([]);
	const [availCourses, filterCourses] = useState([]);

	// TO RETURN ALL CHOSEN COURSES IN 'SELECTED COURSES'
	useEffect(() => {
		GetCourses();
	}, []);

	const GetCourses = () => {
		fetch(api_base + '/courses')
			.then(res => res.json())
			.then(data => insertCourse(data))
			.catch((err) => console.error("Error: ", err));
	}

	// TO RETURN ALL COURSES IN DATABASE
	useEffect(() => {
		fetchCourses();
	}, []);

	const fetchCourses = () => {
		fetch(api_base + '/courses/all')
			.then(res => res.json())
			.then(data => setCourses(data))
			.catch((err) => console.error("Error: ", err));
	}

	// TO RETURN ALL AVAILABLE COURSES IN DATABASE ACCORDING TO SELECTED TERM
	const fetchAvailCourses = term => {
		const availCourses = courses.filter(course => course.term === term);
		return availCourses;
	}

	// TO ADD ONE SPECIFIC COURSE TO THE ARRAY 'SELECTEDCOURSES'
    const addCourse = async () => {
        const obj = JSON.parse(newCourse);
		const data = await fetch(api_base + '/course/new/' + obj._id, { method: "POST" })
			.then(res => res.json());

		insertCourse(selectedCourses => [...selectedCourses, data]);
		setPopupActive(false);
		setNewCourse("");
    }

	const verifyCourse = course => {
		if (selectedCourses.length === 0 || course.term === selectedCourses[0].term) {
			setNewCourse(course);
			return true;
		} else {
			return false;
		}
	}

	// TO DELETE ONE SPECIFIC COURSE FROM THE ARRAY 'SELECTEDCOURSES'
	const deleteCourse = async id => {
		await fetch(api_base + '/course/delete/' + id, { method: "DELETE" })
			.then(res => res.json());

		insertCourse(selectedCourses => selectedCourses.filter(course => course._id !== id));
	}

	const disablePopupButton = event => {
		event.currentTarget.disabled = true;
	}

	const enablePopupButton = () => {
		const popupButton = document.getElementById("chooseSemButton");
		popupButton.disabled = false;
	}
	if (selectedCourses.length === 0) {
		enablePopupButton();
	}

  return (
    <div className="App">
			<h1>Welcome to the Schedule Generator!</h1>
			{ selectedCourses.length === 0 && <h2>Start by selecting a semester below</h2> }
			{ selectedCourses.length > 0 && <h4>Your courses</h4> }

			<div className="courses">
				{selectedCourses.length > 0 ? selectedCourses.map(course => (
					<div className={"course"} key={course._id}>

						<div className="text">{course.code}</div>

						<div className="delete-course" onClick={() => deleteCourse(course._id)}>x</div>
					</div>
				)) : (
					<p></p>
				)}
            </div>
			<div>
				<div>
					<button className="chooseSemButton" id="chooseSemButton" onClick={e => { setSemPopupActive(true); disablePopupButton(e); }}>Select a Semester</button>
					{sempopupActive ? (
						<div className="sempopup">
						<div className="closeSemPopup" onClick={() => setSemPopupActive(false)}>X</div>
						<div className="semContent">
							<select className="choose-semester-input"
								onChange={e => filterCourses(fetchAvailCourses(e.target.value))}>
								<option selected disabled={true}> -- Select a Semester Below -- </option>
								<option value="1">Semester 1</option>
								<option value="2">Semester 2</option>
								<option value="3">Special Term I</option>
								<option value="4">Special Term II</option>
							</select>
							{availCourses.length > 0 && 
								<div className="semButton" onClick={() => setSemPopupActive(false)}>Confirm Semester</div>
								// once "confirm semester" is clicked, "select a semester" should not appear again
							}
						</div>
						</div>
				) : ''}
				</div>
			</div>

			<div>
			{availCourses.length > 0 && sempopupActive === false &&
				<div className="addPopup" onClick={() => setPopupActive(true)}>+</div>
			}

			{popupActive ? (
					<div className="popup">
						<div className="closePopup" onClick={() => setPopupActive(false)}>X</div>
						<div className="content">
							<h3>Select to Add Course</h3>
							<select className="add-course-input"
								onChange={e => verifyCourse(e.target.value) ? '' : <div>Please Choose a Course from the Same Semester</div>}>
								<option selected disabled={true}>-- Select a Course Below --</option>
								{availCourses.map(course => (
									<option value={JSON.stringify(course)}>
										{course.code}
									</option>))
								}
							</select>
							<div className="button" onClick={addCourse}>Add Course</div>
						</div>
					</div>
				) : ''}
			</div>

	</div>
  );
}

export default App;

