import { useEffect, useState } from 'react';
const api_base = 'http://localhost:3001';
// const { allCourses } = require('./server');

function App() {
	const [popupActive, setPopupActive] = useState(false);
	const [newCourse, setNewCourse] = useState({});
	const [selectedCourses, insertCourse] = useState([]);
	const [courses, setCourses] = useState([]);

	useEffect(() => {
		GetCourses();
	}, []);

	const GetCourses = () => {
		fetch(api_base + '/courses')
			.then(res => res.json())
			.then(data => insertCourse(data))
			.catch((err) => console.error("Error: ", err));
	}

	useEffect(() => {
		fetchCourses();
	}, []);

	const fetchCourses = () => {
		fetch(api_base + '/courses/all')
			.then(res => res.json())
			.then(data => setCourses(data))
			.catch((err) => console.error("Error: ", err));
	}

		/* const getAllCourses = () => {
		const allCourses = fetch(api_base + '/courses/all', { method: "GET" })
			.then(res => res.json())
			.then(() => { return getAllCourses; });

		const printAllCourses = async () => {
			const a = await allCourses;
			console.log(a);
		};

		return printAllCourses();
	} */

	/* const completeCourse = async id => {
		const data = await fetch(api_base + '/course/complete/' + id).then(res => res.json());

        setCourses(courses => courses.map(course => {
            if (course._id === data._id) {
                course.complete = data.complete;
            }

            return course;
        }))}; */

    /* const addCourse = async () => {
        const data = await fetch(api_base + "/course/new", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json" 
                },
                body: JSON.stringify({
                    code: newCourse
                })
            }).then(res => res.json());

        setNewCourse([...selectedCourses, data]);

        setPopupActive(false);
        setNewCourse("");
    } */

    const addCourse = async () => {
        const data = await fetch(api_base + "/course/new/" + newCourse._id, { method: "POST" })
			.then(res => res.json());

        insertCourse([...selectedCourses, data]);

        setPopupActive(false);
        setNewCourse({});
    }

	const deleteCourse = async id => {
		const data = await fetch(api_base + '/course/delete/' + id, { method: "DELETE" })
			.then(res => res.json());

		insertCourse(selectedCourses => selectedCourses.filter(course => course._id !== data.result._id));
	}

  return (
    <div className="App">
			<h1>Welcome!</h1>
			<h4>Your courses</h4>

			<div className="courses">
				{selectedCourses.length > 0 ? selectedCourses.map(course => (
					<div className={"course"} key={course._id}>

						<div className="text">{course.code}</div>

						<div className="delete-course" onClick={() => deleteCourse(course._id)}>x</div>
					</div>
				)) : (
					<p>You currently have no courses, start adding some!</p>
				)}
            </div>

			<div className="addPopup" onClick={() => setPopupActive(true)}>+</div>

			{popupActive ? (
				<div className="popup">
					<div className="closePopup" onClick={() => setPopupActive(false)}>X</div>
					<div className="content">
						<h3>Select to Add Course</h3>
						<select type="text" className="add-course-input"
							onChange={e => setNewCourse(e.target.value)}
							value={newCourse}>
							{courses.map(course => <option key={course._id} value={course}>
            						{course.code}
								</option>)}
						</select>
						<div className="button" onClick={addCourse}>Add Course</div>
					</div>
				</div>
			) : ''}
		</div>
  );
}

export default App;
