import { ButtonGroup, Button, ArrowDropDownIcon, MenuItem, MenuList } from '@mui/material';
import { useEffect, useState } from 'react';
const api_base = 'http://localhost:3001';

function App() {
	const [courses, setCourses] = useState([]);
	const [popupActive, setPopupActive] = useState(false);
	const [newCourse, setNewCourse] = useState("");
	const [selectedCourses, insertCourse] = useState([]);
	const anchorRef = React.useRef<HTMLDivElement>(null);

	useEffect(() => {
		GetCourses();
	}, []);

	const GetCourses = () => {
		fetch(api_base + '/courses')
			.then(res => res.json())
			.then(data => insertCourse(data))
			.catch((err) => console.error("Error: ", err));
	}

	/* const completeCourse = async id => {
		const data = await fetch(api_base + '/course/complete/' + id).then(res => res.json());

        setCourses(courses => courses.map(course => {
            if (course._id === data._id) {
                course.complete = data.complete;
            }

            return course;
        }))}; */

    const addCourse = async () => {
        const data = await fetch(api_base + "/course/new", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json" 
                },
                body: JSON.stringify({
                    code: newCourse
                })
            }).then(res => res.json());

            insertCourse([...selectedCourses, data]);

            setPopupActive(false);
            setNewCourse("");
    }

	const deleteCourse = async id => {
		const data = await fetch(api_base + '/course/delete/' + id, { method: "DELETE" }).then(res => res.json());

		insertCourse(selectedCourses => selectedCourses.filter(course => course._id !== data.result._id));
	}

	const options = courses;

	const handleClick = () => {
		console.info(`You clicked ${options[newCourse]}`);
	};

  return (
    <div className="App">
			<h1>Welcome!</h1>
			<h4>Your courses</h4>

			<div className="courses">
				{courses.length > 0 ? courses.map(course => (
					<div className={
						"course" 
						// + (course.complete ? " is-complete" : "")
					} key={course._id} 
					// onClick={() => completeCourse(course._id)}
					>
						<div className="checkbox"></div>

						<div className="text">{course.text}</div>

						<div className="delete-course" onClick={() => deleteCourse(course._id)}>x</div>
					</div>
				)) : (
					<p>You currently have no courses, start adding some!</p>
				)}
            </div>

			<div className="addPopup" onClick={() => setPopupActive(true)}>+</div>

			{popupActive ? (
				<div className="popup">
					<div className="closePopup" onClick={() => setPopupActive(false)}>X</div>
					<div className="content">
						<h3>Add Course</h3>
						<ButtonGroup variant="contained" ref={anchorRef} aria-label="split button">
        					<Button onClick={handleClick}>{options[newCourse]}</Button>
						</ButtonGroup>
						<input type="text" className="add-course-input" onChange={e => setNewCourse(e.target.value)} value={newCourse} />
						<div className="button" onClick={addCourse}>Add Course</div>
					</div>
				</div>
			) : ''}
		</div>
  );
}

export default App;
