import { useEffect, useState } from 'react';
const api_base = 'http://localhost:3001';

function App() {
	const [popupActive, setPopupActive] = useState(false);
	const [sempopupActive, setSemPopupActive] = useState(false);
	const [localStorageUpdated, setLocalStorageUpdated] = useState(false);

	// const [newCourse, setNewCourse] = useState(""); // replaced by "newCourse" in LS
	// const [selectedCourses, insertCourse] = useState([]); // replaced by "selected" in LS
	// eslint-disable-next-line  no-unused-vars
	const [courses, setCourses] = useState([]);
	// const [availCourses, filterCourses] = useState([]); // replaced by "term1", "term2"... in LS

	localStorage.setItem("selected", JSON.stringify([]));
	localStorage.setItem("newCourse", "");
	localStorage.setItem("term", "0");

	// TO RETURN ALL CHOSEN COURSES IN 'SELECTED COURSES'
	useEffect(() => {
		GetCourses();
	}, []);

	const GetCourses = () => {
		return JSON.parse(localStorage.getItem("selected"));
	}

	// TO RETURN ALL COURSES IN DATABASE
	useEffect(() => {
		fetchCourses();
	}, []);

	const fetchCourses = () => {
		fetch(api_base + '/courses/all')
			.then(res => res.json())
			.then(data => setCourses(data))
			.catch((err) => console.error("Error: ", err));
	}

	// setting up available courses
	localStorage.setItem("term1", JSON.stringify(courses.filter(course => course.term === "1")));
	localStorage.setItem("term2", JSON.stringify(courses.filter(course => course.term === "2")));
	localStorage.setItem("term3", JSON.stringify(courses.filter(course => course.term === "3")));
	localStorage.setItem("term4", JSON.stringify(courses.filter(course => course.term === "4")));

	const updateNewCourse = course => {
		localStorage.setItem("newCourse", course);
	}

	// TO ADD ONE SPECIFIC COURSE TO THE ARRAY 'SELECTEDCOURSES' PROVIDED IT IS NOT ALREADY ADDED
    const addCourse = async () => {
		const obj = JSON.parse(localStorage.getItem("newCourse")); // obj is course to be added
		if (!(JSON.parse(localStorage.getItem("selected")).some(object => (object._id === obj._id)))) {
			var existing = JSON.parse(localStorage.getItem("selected")); // array of selected courses
			existing.push(obj);
			localStorage.setItem("selected", JSON.stringify(existing));
		}
		setPopupActive(false);
		updateNewCourse("");
    }

	// TO DELETE ONE SPECIFIC COURSE FROM THE ARRAY 'SELECTEDCOURSES'
	const deleteCourse = async id => {
		var existing = JSON.parse(localStorage.getItem("selected")); // array of selected courses
		existing.filter(course => course._id !== id)
		localStorage.setItem("selected", JSON.stringify(existing));
	}

	const saveTerm = term => {
		localStorage.setItem("term", term);
	}

	const isTermChosen = () => {
		return localStorage.getItem("term") !== "0";
	}

	/* useEffect(() => {
		window.addEventListener("storage", () => {
			console.log(JSON.parse(window.localStorage.getItem("term")));
			console.log(JSON.parse(window.localStorage.getItem("selected")));
			console.log(JSON.parse(window.localStorage.getItem("newCourse")));
		});
	}, []); */

	useEffect(() => {
		// Event listener callback
		const handleStorageChange = () => {
		  setLocalStorageUpdated(true);
		};

		// Add event listener to 'storage' event
		window.addEventListener('storage', handleStorageChange);

		// Cleanup function
		return () => {
		  window.removeEventListener('storage', handleStorageChange);
		};
	  }, []);

	  useEffect(() => {
		if (localStorageUpdated) {
		  // Perform necessary actions or update state based on the changes
			GetCourses();
			isTermChosen();

		  // Reset the localStorage update flag
		  setLocalStorageUpdated(false);
		}
	  }, [localStorageUpdated]);

  return (
    <div className="App">
			<h1>Welcome to the Schedule Generator!</h1>
			{ GetCourses().length === 0 && <h2>Start by selecting a semester below</h2> }
			{ GetCourses().length > 0 && 
				( (GetCourses()[0].term === "1" && <h4>Your Semester 1 Courses</h4>) ||
				(GetCourses()[0].term === "2" && <h4>Your Semester 2 Courses</h4>) ||
				(GetCourses()[0].term === "3" && <h4>Your Special Term I Courses</h4>) ||
				(GetCourses()[0].term === "4" && <h4>Your Special Term II Courses</h4>) ) }

			<div className="courses">
				{GetCourses().length > 0 
					? GetCourses().map(course => (
						<div className={"course"} key={course._id}>

							<div className="text">{course.code}</div>

							<div className="delete-course" onClick={() => deleteCourse(course._id)}>x</div>
						</div>
				)) : (
					<p></p>
				)}
            </div>
			<div>
				{GetCourses().length === 0 && <div>
					<button className="SemButton" id="chooseSemButton" onClick={e => { setSemPopupActive(true); }}>Select a Semester</button>
					{sempopupActive ? (
						<div className="sempopup">
						<div className="closeSemPopup" onClick={() => setSemPopupActive(false)}>X</div>
						<div className="semContent">
							<select className="choose-semester-input"
								onChange={e => saveTerm(e.target.value)}>
								<option selected disabled={true}> -- Select a Semester Below -- </option>
								<option value="1">Semester 1</option>
								<option value="2">Semester 2</option>
								<option value="3">Special Term I</option>
								<option value="4">Special Term II</option>
							</select>
							{isTermChosen() && 
								<div className="semButton" onClick={() => setSemPopupActive(false)}>Confirm Semester</div>
							}
						</div>
						</div>
				) : ''}
				</div> }
			</div>

			<div>
			{localStorage.getItem("term") !== "0" && sempopupActive === false &&
				<div className="addPopup" onClick={() => setPopupActive(true)}>+</div>
			}

			{popupActive ? (
					<div className="popup">
						<div className="closePopup" onClick={() => setPopupActive(false)}>X</div>
						<div className="content">
							<h3>Select to Add Course</h3>
							<select className="add-course-input"
								onChange={e => updateNewCourse(e.target.value)}>
								<option selected disabled={true}>-- Select a Course Below --</option>
								{JSON.parse(localStorage.getItem("term" + localStorage.getItem("term"))).map(course => (
									<option value={JSON.stringify(course)}>
										{course.code}
									</option>))
								}
							</select>
							<div className="button" onClick={addCourse}>Add Course</div>
						</div>
					</div>
				) : ''}
			</div>
	</div>
  );
}

export default App;
